import React, { useState, useEffect } from "react";
import { addBook, getBooks, deleteBook } from "../utils/bookService";
import { useNavigate } from "react-router-dom";

const AdminPanel = () => {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: "", author: "", description: "" });
  const navigate = useNavigate();

  useEffect(() => {
    setBooks(getBooks());
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    addBook(form);
    setBooks(getBooks());
    setForm({ title: "", author: "", description: "" });
  };

  const handleDelete = (id) => {
    deleteBook(id);
    setBooks(getBooks());
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h2>ðŸ›  Admin Panel</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: "2rem" }}>
        <input
          placeholder="Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          required
          style={{ display: "block", marginBottom: "10px" }}
        />
        <input
          placeholder="Author"
          value={form.author}
          onChange={(e) => setForm({ ...form, author: e.target.value })}
          required
          style={{ display: "block", marginBottom: "10px" }}
        />
        <textarea
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          required
          style={{ display: "block", marginBottom: "10px" }}
        />
        <button type="submit">Add Book</button>
      </form>

      {books.map((book) => (
        <div key={book.id} style={{ marginBottom: "1rem" }}>
          <strong>{book.title}</strong> â€” {book.author}
          <button onClick={() => navigate(`/edit/${book.id}`)} style={{ marginLeft: "10px" }}>
            Edit
          </button>
          <button onClick={() => handleDelete(book.id)} style={{ marginLeft: "10px" }}>
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default AdminPanel;
